mapper.select:
select id, order_id, product_id, order_amount, order_pay, real_pay, order_status,
added_by_name, added_time, last_modified_by_name, last_modified_time, valid
from t_order
where valid = 'T'

  and product_id = #{productId, jdbcType=VARCHAR}


  and order_status = #{orderStatus, jdbcType=VARCHAR}


  and order_id = #{orderId, jdbcType=VARCHAR}

order by id DESC
limit 1

select t1.id id, t1.order_id orderId, t1.product_id productId, t1.order_amount orderAmount, t1.order_pay orderPay, t1.real_pay realPay, t2.product_name productName, t2.describute describute, t2.price price, t2.product_img_src productImgSrc, t3.product_amount productAmount from t_order t1 left join t_product_info t2 on t2.product_id = t1.product_id and t2.valid = 'T' left join t_storage_info t3 on t3.product_id = t2.product_id and t3.valid = 'T' where t1.valid = 'T'
and t1.order_id = #{map.orderId, jdbcType=VARCHAR}
and t1.order_status = #{map.orderType, jdbcType=VARCHAR}
order by id DESC
