mapper.select:
select id, storage_id, product_id, storage_name, storage_size, product_amount, rule, remark, added_name, added_time, last_modified_by_name, last_modified_time, valid from t_storage_info where valid = 'T'
and storage_id = #{storageId, jdbcType=VARCHAR}
and product_id = #{productId, jdbcType=VARCHAR}
order by id DESC limit 1
